"use client";

import React, { forwardRef, useRef, useEffect, useState } from "react";
import { motion } from "framer-motion";

import { cn } from "@/lib/utils";
import { AnimatedBeam } from "./AnimatedBeam";

const Circle = forwardRef<
  HTMLDivElement,
  {
    className?: string;
    icon?: React.ReactNode;
    fullLogo?: React.ReactNode;
    logoWidth?: number;
    hoverScale?: number;
  }
>(({ className, icon, fullLogo, logoWidth = 120, hoverScale = 3 }, ref) => {
  const [isHovered, setIsHovered] = useState(false);
  const hasFullLogo = !!fullLogo;

  return (
    <motion.div
      ref={ref}
      className={cn(
        "flex items-center justify-center border-2 border-black bg-white shadow-[0_15px_40px_-15px_rgba(0,0,0,1)] relative overflow-visible origin-center",
        className
      )}
      style={{
        width: 75,
        height: 75,
        borderRadius: 37.5,
      }}
      animate={
        isHovered && hasFullLogo
          ? {
              scaleX: logoWidth / 75,
              borderRadius: 8,
              zIndex: 5,
            }
          : {
              scaleX: 1,
              borderRadius: 37.5,
              zIndex: 10,
            }
      }
      transition={{
        duration: 0.4,
        ease: [0.23, 1, 0.32, 1],
      }}
      onHoverStart={() => hasFullLogo && setIsHovered(true)}
      onHoverEnd={() => hasFullLogo && setIsHovered(false)}
    >
      <motion.div
        className="absolute inset-0 flex items-center justify-center"
        style={{ scaleX: isHovered ? 75 / logoWidth : 1 }}
        animate={{
          opacity: isHovered ? 0 : 1,
        }}
        transition={{ duration: 0.3 }}
      >
        {icon}
      </motion.div>
      <motion.div
        className="absolute inset-0 flex items-center justify-center px-4"
        style={{
          scaleX: isHovered ? 75 / logoWidth : 1,
          scale: isHovered ? hoverScale : 1,
        }}
        animate={{
          opacity: isHovered ? 1 : 0,
        }}
        transition={{ duration: 0.3, delay: isHovered ? 0.1 : 0 }}
      >
        {fullLogo}
      </motion.div>
    </motion.div>
  );
});

Circle.displayName = "Circle";

export function AnimatedBeamMultipleOutput({
  className,
  isVisible = true,
}: {
  className?: string;
  isVisible?: boolean;
}) {
  const containerRef = useRef<HTMLDivElement>(null);
  const div1Ref = useRef<HTMLDivElement>(null);
  const div2Ref = useRef<HTMLDivElement>(null);
  const div3Ref = useRef<HTMLDivElement>(null);
  const div4Ref = useRef<HTMLDivElement>(null);
  const div6Ref = useRef<HTMLDivElement>(null);
  const div7Ref = useRef<HTMLDivElement>(null);

  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    if (isVisible) {
      const timer = setTimeout(() => {
        setMounted(true);
      }, 300); // Wait for reveal animation (1000ms) to mostly complete

      return () => clearTimeout(timer);
    }
  }, [isVisible]);

  return (
    <div
      className={cn(
        "relative flex w-full items-center justify-center overflow-hidden px-10 md:px-20 lg:px-40 py-10",
        className
      )}
      ref={containerRef}
    >
      <div className="flex size-full flex-row items-stretch justify-between gap-8">
        <div className="flex flex-col justify-center gap-2">
          <Circle
            ref={div1Ref}
            icon={<Icons.elevenlabs />}
            fullLogo={<Icons.elevenlabsFull />}
            logoWidth={180}
          />
          <Circle
            ref={div2Ref}
            icon={<Icons.anthropic />}
            fullLogo={<Icons.anthropicFull />}
            logoWidth={180}
          />
          <Circle
            ref={div3Ref}
            icon={<Icons.openai />}
            fullLogo={<Icons.openaiFull />}
            logoWidth={180}
            hoverScale={2}
          />
          <Circle
            ref={div4Ref}
            icon={<Icons.deepgram />}
            fullLogo={<Icons.deepgramFull />}
            logoWidth={180}
          />
        </div>
        <div className="flex flex-col justify-center">
          <Circle
            ref={div6Ref}
            icon={<Icons.odee />}
            fullLogo={<Icons.odeeFull />}
            logoWidth={180}
            hoverScale={2}
          />
        </div>
        <div className="flex flex-col justify-center">
          <Circle ref={div7Ref} icon={<Icons.user />} />
        </div>
      </div>

      {mounted && (
        <>
          <AnimatedBeam
            containerRef={containerRef}
            fromRef={div1Ref}
            toRef={div6Ref}
          />
          <AnimatedBeam
            containerRef={containerRef}
            fromRef={div2Ref}
            toRef={div6Ref}
          />
          <AnimatedBeam
            containerRef={containerRef}
            fromRef={div3Ref}
            toRef={div6Ref}
          />
          <AnimatedBeam
            containerRef={containerRef}
            fromRef={div4Ref}
            toRef={div6Ref}
          />
          <AnimatedBeam
            containerRef={containerRef}
            fromRef={div6Ref}
            toRef={div7Ref}
          />
        </>
      )}
    </div>
  );
}

